import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import './Expenses.css'

const Expenses = (props) => {
    const [filteredYear,setFilteredYear] = useState('2020');
    const filterChangeHandler = (selectedYear) =>
    {
        setFilteredYear(selectedYear);
    }

    const filteredExpenses = props.items.filter(expense => {               
        return expense.date.getFullYear().toString() === filteredYear;
    })
    console.log("filteredExpenses",filteredExpenses.length);


    return (

    <div className="expenses">
    <ExpensesFilter selected={filteredYear}  
    onChangeFilter={filterChangeHandler}/>

    {filteredExpenses.length === 0  && <p>No Expense Found</p>}  
    {filteredExpenses.length > 0 &&   
                                                    // also use uninary expretion
        filteredExpenses.map((expense) => (       // && => first condition is met(filteredExpenses.length === 0) it move to second condition(No Expense Found) and retun value
        <ExpenseItem
        key={expense.id}                        //unique id which helps react identify each item,provide better performance,prevenet updation of all item
        title = {expense.title}
        amount = {expense.amount}
        date = {expense.date}
        />
     ))}  
    </div>
    )
}

export default Expenses;
